var app=angular.module("IotDemoApp",["ui.bootstrap"]);app.run(function(){});app.controller("DeviceEmulatorController",["$rootScope","$scope","$http","$timeout",function(n,t,i){t.refresh=function(){i.get("api/Devices").then(function(n){t.creatingDevices=n.data.creatingDevices;t.sendingData=n.data.sendingData})};t.toggleCreateDevices=function(){i.put("api/Devices/ToggleCreate").then(function(){t.refresh()})};t.toggleSendDataFromDevices=function(){i.put("api/Devices/togglesend").then(function(){t.refresh()})}}]);